var heightH = $(".header_sp_first").height()
  , heightDoc = $(document).height();
jQuery(document).ready(function(e) {
    heightDoc = e(document).height(),
    heightH = e(window).width() < 768 ? e(".visible-xs .header_sp_first").height() : e("header.header .header_sp_first").height(),
    e('a[href*="#"]:not([href="#"])').click(function() {
        if (location.pathname.replace(/^\//, "") == this.pathname.replace(/^\//, "") && location.hostname == this.hostname) {
            var s = e(this.hash);
            if (s = s.length ? s : e("[name=" + this.hash.slice(1) + "]"),
            s.length)
                return e("html, body").animate({
                    scrollTop: s.offset().top - heightH
                }, 500),
                e(".header_sp_second").removeClass("active"),
                e(".header_sp_first").removeClass("active"),
                e(".header_sp_first__r__btnm__over span").removeClass("active"),
                !1
        }
    })
}),
$(".header_sp_first__r__btnm").click(function(e) {
    $(this).find("span").each(function() {
        $(this).toggleClass("active")
    }),
    $(".header_sp_second,.header_sp_first").toggleClass("active")
}),
$(".header_sp_second__wrap p.btn_close").click(function() {
    $(".header_sp_first__r__btnm span").hasClass("active") && $(".header_sp_first__r__btnm span").removeClass("active"),
    $(".header_sp_second,.header_sp_first").hasClass("active") && $(".header_sp_second,.header_sp_first").removeClass("active")
});
